                    if 'barChart' in chart_type.lower() or 'columnChart' in chart_type.lower() or 'bar' in chart_type.lower() or 'column' in chart_type.lower():
                        has_bar_chart = True
                        chart_found = True
                        logger.info(f"✓ Chart {chart_idx + 1} is a bar/column chart (openpyxl)")
                        
