                                # Normalize ranges for comparison (remove $ and sheet names)
                                def normalize_range(r):
                                    if not r:
                                        return None
                                    r = r.replace('$', '')
                                    if '!' in r:
                                        r = r.split('!')[1]
                                    return r.upper()
                                
                                x_range_norm = normalize_range(x_range)
