        logger.info(f"Found {len(charts)} chart(s) in the worksheet")
        
        # Check chart via XML
        chart_ns = {'c': 'http://schemas.openxmlformats.org/drawingml/2006/chart'}
        column_chart_found = False
        line_chart_found = False
        scatter_chart_found = False
