                                                            logger.info(f"        X error bar value (from c:val): {x_err_val}")
                                                            # According to XML example, X error may have fixed value 1
                                                            # This might indicate swapped config (X should have range B3:B10, Y should have value 1)
                                                            # But if Y error already has range B3:B10, we can accept X error having value 1
                                                            if abs(x_err_val - series3_y_error_value) < 0.01:
                                                                logger.info(f"        ⚠ X error bar has fixed value {x_err_val} (may indicate swapped config)")
                                                                # Accept this as swapped config if Y error already accepted swapped config
                                                                if verification_results.get('series3_y_error_value_ok', False):
                                                                    verification_results['series3_x_error_range_ok'] = True
                                                                    verification_results['series3_x_error_direction_ok'] = True
                                                                    x_err_range_found = True
