                            # Check if this is an ofPieChart (compound pie chart)
                            of_pie_charts = root.xpath('.//c:ofPieChart', namespaces=chart_ns)
                            if not of_pie_charts:
                                logger.info("This chart is not an ofPieChart (compound pie chart), skipping")
